import React, { useState, useEffect } from 'react';

const ItemComponent = ({
  name,
  content,
  isFolder,
  onContentSave,
  onRename,
  onDelete,
  onCopy,
  onCut,
  onPaste
}) => {
  const [isEditing, setIsEditing] = useState(false);
  const [isRenaming, setIsRenaming] = useState(false);
  const [editContent, setEditContent] = useState(content);
  const [newName, setNewName] = useState(name);

  const handleSave = () => {
    if (!isFolder) {
      onContentSave(name, editContent);
      setIsEditing(false);
    }
  };

  const handleRename = () => {
    onRename(name, newName);
    setIsRenaming(false);
  };

  return (
    <div style={{ margin: '10px 0', paddingLeft: isFolder ? '20px' : '40px' }}>
      {isRenaming ? (
        <>
          <input value={newName} onChange={(e) => setNewName(e.target.value)} />
          <button onClick={handleRename}>Rename</button>
          <button onClick={() => setIsRenaming(false)}>Cancel</button>
        </>
      ) : (
        <>
          <h3>{name}</h3>
          <button onClick={() => setIsRenaming(true)}>Rename</button>
          <button onClick={() => onDelete(name)}>Delete</button>
          {isFolder && <button onClick={() => onPaste(name)}>Paste Here</button>}
          {!isFolder && <button onClick={() => setIsEditing(true)}>Edit</button>}
          {!isFolder && <button onClick={() => onCopy(name)}>Copy</button>}
          {!isFolder && <button onClick={() => onCut(name)}>Cut</button>}
        </>
      )}
      {isEditing && !isFolder && (
        <>
          <textarea value={editContent} onChange={(e) => setEditContent(e.target.value)} />
          <button onClick={handleSave}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </>
      )}
    </div>
  );
};

function App() {
  const [fileSystem, setFileSystem] = useState(
    JSON.parse(localStorage.getItem('fileSystem')) || {}
  );
  const [newItemName, setNewItemName] = useState('');
  const [clipboard, setClipboard] = useState(
    JSON.parse(localStorage.getItem('clipboard')) || null
  );
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    localStorage.setItem('fileSystem', JSON.stringify(fileSystem));
  }, [fileSystem]);

  useEffect(() => {
    localStorage.setItem('clipboard', JSON.stringify(clipboard));
  }, [clipboard]);

  // All state update functions: addNewItem, updateFileContent, deleteItem, renameItem, copyItem, cutItem, pasteItem

  const pasteItem = (targetFolderName) => {
    if (!clipboard) return;

    const { itemName, content, action } = clipboard;
    const newFileSystem = { ...fileSystem };

    // Determine the target for pasting
    const target = targetFolderName ? newFileSystem[targetFolderName] : newFileSystem;

    // Check if the item already exists at the target location
    if (target[itemName]) {
      alert('An item with this name already exists at the target location.');
      return;
    }

    // Perform the paste operation
    target[itemName] = content;

    if (action === 'cut') {
      // When cutting, we need to remove the item from its original location
      if (targetFolderName) {
        delete newFileSystem[clipboard.itemName];
      } else {
        Object.keys(newFileSystem).forEach(key => {
          if (typeof newFileSystem[key] === 'object' && newFileSystem[key][itemName]) {
            delete newFileSystem[key][itemName];
          }
        });
      }
      setClipboard(null);
    }

    setFileSystem(newFileSystem);
  };

  const renderFileSystem = (fs, parentPath = '') => {
    return Object.keys(fs)
      .filter((key) => key.toLowerCase().includes(searchQuery.toLowerCase()))
      .map((key) => {
        const isFolder = typeof fs[key] === 'object';
        const fullPath = parentPath ? `${parentPath}/${key}` : key;
        return (
          <ItemComponent
            key={fullPath}
            name={key}
            isFolder={isFolder}
            content={isFolder ? null : fs[key]}
            onContentSave={isFolder ? null : (name, content) => updateFileContent(name, content)}
            onRename={(oldName, newName) => renameItem(oldName, newName)}
            onDelete={() => deleteItem(key)}
            onCopy={isFolder ? null : () => copyItem(key)}
            onCut={isFolder ? null : () => cutItem(key)}
            onPaste={isFolder ? (folderName) => pasteItem(folderName) : null}
          >
            {isFolder && renderFileSystem(fs[key], fullPath)}
          </ItemComponent>
        );
      });
  };

  return (
    <div>
      <input
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        placeholder="Search files and folders"
      />
      <input
        type="text"
        value={newItemName}
        onChange={(e) => setNewItemName(e.target.value)}
        placeholder="Enter new item name"
      />
      <button onClick={() => addNewItem(true)}>Add Folder</button>
      <button onClick={() => addNewItem(false)}>Add File</button>
      {clipboard && <button onClick={() => pasteItem(null)}>Paste into root</button>}
      <div style={{ marginTop: '20px' }}>
        {renderFileSystem(fileSystem)}
      </div>
    </div>
  );
}

export default App;
