import React, { useState, useEffect } from 'react';

const ItemComponent = ({
  name,
  content,
  isFolder,
  onContentSave,
  onRename,
  onDelete,
  onCopy,
  onCut,
  onPaste
}) => {
  const [isEditing, setIsEditing] = useState(false);
  const [isRenaming, setIsRenaming] = useState(false);
  const [editContent, setEditContent] = useState(content);
  const [newName, setNewName] = useState(name);

  const handleSave = () => {
    if (!isFolder) {
      onContentSave(name, editContent);
      setIsEditing(false);
    }
  };

  const handleRename = () => {
    onRename(name, newName);
    setIsRenaming(false);
  };

  return (
    <div style={{ margin: '10px 0', paddingLeft: isFolder ? '20px' : '40px' }}>
      {isRenaming ? (
        <>
          <input value={newName} onChange={(e) => setNewName(e.target.value)} />
          <button onClick={handleRename}>Rename</button>
          <button onClick={() => setIsRenaming(false)}>Cancel</button>
        </>
      ) : (
        <>
          <h3>{name}</h3>
          <button onClick={() => setIsRenaming(true)}>Rename</button>
          <button onClick={() => onDelete(name)}>Delete</button>
          {isFolder && <button onClick={() => onPaste(name)}>Paste Here</button>}
          {!isFolder && <button onClick={() => setIsEditing(true)}>Edit</button>}
          {!isFolder && <button onClick={() => onCopy(name)}>Copy</button>}
          {!isFolder && <button onClick={() => onCut(name)}>Cut</button>}
        </>
      )}
      {isEditing && !isFolder && (
        <>
          <textarea value={editContent} onChange={(e) => setEditContent(e.target.value)} />
          <button onClick={handleSave}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </>
      )}
    </div>
  );
};

function App() {
  const [fileSystem, setFileSystem] = useState(
    JSON.parse(localStorage.getItem('fileSystem')) || {}
  );
  const [newItemName, setNewItemName] = useState('');
  const [clipboard, setClipboard] = useState(
    JSON.parse(localStorage.getItem('clipboard')) || null
  );
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    localStorage.setItem('fileSystem', JSON.stringify(fileSystem));
  }, [fileSystem]);

  useEffect(() => {
    localStorage.setItem('clipboard', JSON.stringify(clipboard));
  }, [clipboard]);
  const addNewItem = (isFolder) => {
    const newFileSystem = { ...fileSystem };
    if (newFileSystem[newItemName]) {
      alert('An item with this name already exists.');
      return;
    }
    newFileSystem[newItemName] = isFolder ? {} : 'New content';
    setFileSystem(newFileSystem);
    setNewItemName('');
  };

  const updateFileContent = (fileName, content) => {
    const newFileSystem = { ...fileSystem };
    newFileSystem[fileName] = content;
    setFileSystem(newFileSystem);
  };

  const deleteItem = (itemName) => {
    const newFileSystem = { ...fileSystem };
    delete newFileSystem[itemName];
    setFileSystem(newFileSystem);
    if (clipboard && clipboard.itemName === itemName) {
      setClipboard(null);
    }
  };

  const renameItem = (oldName, newName) => {
    if (oldName !== newName && newName.trim() !== '') {
      const newFileSystem = { ...fileSystem };
      if (newFileSystem[newName]) {
        alert('An item with this new name already exists.');
        return;
      }
      newFileSystem[newName] = newFileSystem[oldName];
      delete newFileSystem[oldName];
      setFileSystem(newFileSystem);
      if (clipboard && clipboard.itemName === oldName) {
        setClipboard({ ...clipboard, itemName: newName });
      }
    }
  };

  const copyItem = (itemName) => {
    setClipboard({ itemName, content: fileSystem[itemName], action: 'copy' });
  };

  const cutItem = (itemName) => {
    setClipboard({ itemName, content: fileSystem[itemName], action: 'cut' });
  };

  const pasteItem = (targetFolderName) => {
    if (!clipboard || !fileSystem[targetFolderName]) {
      alert('Paste operation is not possible.');
      return;
    }
    const { itemName, content, action } = clipboard;
    const newFileSystem = { ...fileSystem };
    if (newFileSystem[targetFolderName][itemName]) {
      alert('An item with this name already exists in the target folder.');
      return;
    }
    newFileSystem[targetFolderName][itemName] = content;
    if (action === 'cut') {
      delete newFileSystem[clipboard.itemName];
      setClipboard(null);
    }
    setFileSystem(newFileSystem);
  };

  const renderFileSystem = (fs) => {
    return Object.keys(fs).map((key) => {
      const isFolder = typeof fs[key] === 'object' && fs[key] !== null && !(fs[key] instanceof Array);
      return isFolder ? (
        <FolderComponent
          key={key}
          name={key}
          onDelete={deleteItem}
          onRename={renameItem}
          onPaste={pasteItem}
        >
          {renderFileSystem(fs[key])}
        </FolderComponent>
      ) : (
        <FileComponent
          key={key}
          name={key}
          content={fs[key]}
          onContentSave={updateFileContent}
          onRename={renameItem}
          onDelete={deleteItem}
          onCopy={copyItem}
          onCut={cutItem}
        />
      );
    });
  };

  return (
    <div>
      <input
        type="text"
        value={newItemName}
        onChange={(e) => setNewItemName(e.target.value)}
        placeholder="Enter new item name"
      />
      <button onClick={() => addNewItem(true)}>Add Folder</button>
      <button onClick={() => addNewItem(false)}>Add File</button>
      {clipboard && <button onClick={() => pasteItem('')}>Paste into root</button>}
      <div style={{ marginTop: '20px' }}>
        {renderFileSystem(fileSystem)}
      </div>
    </div>
  );
}

export default App;
