import React, { useState } from 'react';

const FileComponent = ({ name, content, onContentSave }) => {
  const [editContent, setEditContent] = useState(content);
  const [isEditing, setIsEditing] = useState(false);

  const handleSave = () => {
    onContentSave(name, editContent);
    setIsEditing(false); // Hide the textarea on save
  };

  const handleEdit = () => {
    setIsEditing(true); // Show the textarea when editing
  };

  return (
    <div style={{ marginBottom: '20px' }}>
      <h3>{name}</h3>
      {isEditing ? (
        <>
          <textarea
            value={editContent}
            onChange={(e) => setEditContent(e.target.value)}
            rows="6"
            cols="30"
          />
          <br />
          <button onClick={handleSave}>Save</button>
        </>
      ) : (
        <>
          <button onClick={handleEdit}>Edit</button>
        </>
      )}
    </div>
  );
};

const FolderComponent = ({ name, children }) => {
  return (
    <div style={{ marginLeft: '20px' }}>
      <h2>{name}</h2>
      {children}
    </div>
  );
};

function App() {
  const [fileSystem, setFileSystem] = useState({});
  const [newItemName, setNewItemName] = useState('');

  const addNewItem = (isFolder) => {
    const newFileSystem = { ...fileSystem };
    const content = newItemName.endsWith('.txt') || newItemName.endsWith('.js') ||
                    newItemName.endsWith('.ts') || newItemName.endsWith('.json') ? 'test' : '';

    if (newFileSystem[newItemName]) {
      alert('A file or folder with this name already exists.');
      return;
    }

    if (isFolder) {
      newFileSystem[newItemName] = {};
    } else {
      newFileSystem[newItemName] = content;
    }

    setFileSystem(newFileSystem);
    setNewItemName('');
  };

  const updateFileContent = (fileName, content) => {
    const newFileSystem = { ...fileSystem, [fileName]: content };
    setFileSystem(newFileSystem);
  };

  const renderFileSystem = (fs) => {
    return Object.entries(fs).map(([key, value]) => {
      if (typeof value === 'object') {
        return (
          <FolderComponent key={key} name={key}>
            {renderFileSystem(value)}
          </FolderComponent>
        );
      } else {
        return (
          <FileComponent
            key={key}
            name={key}
            content={value}
            onContentSave={updateFileContent}
          />
        );
      }
    });
  };

  return (
    <div>
      <input
        type="text"
        value={newItemName}
        placeholder="New file or folder name"
        onChange={(e) => setNewItemName(e.target.value)}
      />
      <button onClick={() => addNewItem(false)}>Add File</button>
      <button onClick={() => addNewItem(true)}>Add Folder</button>
      <div style={{ marginTop: '20px' }}>
        {renderFileSystem(fileSystem)}
      </div>
    </div>
  );
}

export default App;
