import React, { useState } from 'react';

const FileComponent = ({ name, content, onContentSave, onRename, onDelete }) => {
  const [editContent, setEditContent] = useState(content);
  const [newName, setNewName] = useState(name);
  const [isEditing, setIsEditing] = useState(false);
  const [isRenaming, setIsRenaming] = useState(false);

  const handleSave = () => {
    onContentSave(name, editContent);
    setIsEditing(false);
  };

  const handleRename = () => {
    onRename(name, newName);
    setIsRenaming(false);
  };

  return (
    <div style={{ marginBottom: '20px' }}>
      {isRenaming ? (
        <>
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <button onClick={handleRename}>Rename</button>
          <button onClick={() => setIsRenaming(false)}>Cancel</button>
        </>
      ) : (
        <>
          <h3>{name}</h3>
          {isEditing ? (
            <>
              <textarea
                value={editContent}
                onChange={(e) => setEditContent(e.target.value)}
                rows="6"
                cols="30"
              />
              <br />
              <button onClick={handleSave}>Save</button>
              <button onClick={() => setIsEditing(false)}>Cancel</button>
            </>
          ) : (
            <>
              <button onClick={() => setIsEditing(true)}>Edit</button>
              <button onClick={() => setIsRenaming(true)}>Rename</button>
              <button onClick={() => onDelete(name)}>Delete</button>
            </>
          )}
        </>
      )}
    </div>
  );
};

const FolderComponent = ({ name, onDelete, children }) => {
  return (
    <div style={{ marginLeft: '20px' }}>
      <h2>{name}</h2>
      <button onClick={() => onDelete(name)}>Delete Folder</button>
      {children}
    </div>
  );
};

function App() {
  const [fileSystem, setFileSystem] = useState({});
  const [newItemName, setNewItemName] = useState('');

  const addNewItem = (isFolder) => {
    const newFileSystem = { ...fileSystem };
    if (newFileSystem[newItemName]) {
      alert('A file or folder with this name already exists.');
      return;
    }

    const content = newItemName.endsWith('.txt') || newItemName.endsWith('.js') ||
                    newItemName.endsWith('.ts') || newItemName.endsWith('.json') ? 'test' : '';

    if (isFolder) {
      newFileSystem[newItemName] = {};
    } else {
      newFileSystem[newItemName] = content;
    }

    setFileSystem(newFileSystem);
    setNewItemName('');
  };

  const updateFileContent = (fileName, content) => {
    const newFileSystem = { ...fileSystem };
    newFileSystem[fileName] = content;
    setFileSystem(newFileSystem);
  };

  const renameItem = (oldName, newName) => {
    const newFileSystem = { ...fileSystem };
    if (newFileSystem[newName]) {
      alert('A file or folder with this name already exists.');
      return;
    }
    newFileSystem[newName] = newFileSystem[oldName];
    delete newFileSystem[oldName];
    setFileSystem(newFileSystem);
  };

  const deleteItem = (itemName) => {
    const newFileSystem = { ...fileSystem };
    if (newFileSystem[itemName] === undefined) {
      alert('The file or folder you are trying to delete does not exist.');
      return;
    }
    delete newFileSystem[itemName];
    setFileSystem(newFileSystem);
  };

  const renderFileSystem = (fs) => {
    return Object.entries(fs).map(([key, value]) => {
      if (typeof value === 'object') {
        return (
          <FolderComponent key={key} name={key} onDelete={deleteItem}>
            {renderFileSystem(value)}
          </FolderComponent>
        );
      } else {
        return (
          <FileComponent
            key={key}
            name={key}
            content={value}
            onContentSave={updateFileContent}
            onRename={renameItem}
            onDelete={deleteItem}
          />
        );
      }
    });
  };

  return (
    <div>
      <input
        type="text"
        value={newItemName}
        placeholder="New file or folder name"
        onChange={(e) => setNewItemName(e.target.value)}
      />
      <button onClick={() => addNewItem(false)}>Add File</button>
      <button onClick={() => addNewItem(true)}>Add Folder</button>
      <div style={{ marginTop: '20px' }}>
        {renderFileSystem(fileSystem)}
      </div>
    </div>
  );
}

export default App;
